INSERT INTO customer_feedback (feedback_id, order_id, user_id, overall_rating, product_rating, fulfillment_rating, product_comment, fulfillment_comment, overall_comment, quick_tags, allow_contact, reviewed_status, reviewed_by_user_id, reviewed_at, is_hidden_from_staff, original_feedback_id, created_at, updated_at) VALUES
('fb_low_001', 'order_003', 'user_003', 2, 2, 2, 'The sourdough was undercooked and doughy in the middle. Very disappointing for the price.', 'Delivery took 45 minutes when I was told 35 minutes. Driver seemed rushed and didn''t follow instructions.', 'Not happy with this order. Expected much better quality.', '["cold","late","undercooked"]', true, 'pending_review', NULL, NULL, false, NULL, '2024-01-12T17:00:00Z', '2024-01-12T17:00:00Z'),
('fb_low_002', 'order_005', 'user_005', 1, 1, 3, 'The brownie was completely stale and the cupcake frosting had a strange taste. Inedible!', 'Collection was fine, staff were polite.', 'Very disappointed. The products were not fresh at all. Waste of money.', '["stale","poor_quality","disappointing"]', true, 'pending_review', NULL, NULL, false, NULL, '2024-01-13T16:30:00Z', '2024-01-13T16:30:00Z'),
('fb_low_003', 'order_012', 'user_004', 2, 3, 1, 'Croissants were okay but not as good as expected from reviews.', 'Delivery driver was rude and left the bag on the floor outside. One eclair was completely squashed. No apology.', 'Poor delivery experience ruined the order.', '["damaged","rude_service","late"]', true, 'pending_review', NULL, NULL, false, NULL, '2024-01-13T12:00:00Z', '2024-01-13T12:00:00Z'),
('fb_low_004', 'order_008', 'user_012', 1, 2, 1, 'The lemon drizzle cake was dry and tasteless. Cinnamon rolls were lukewarm.', 'Delivery was 30 minutes late with no communication. Driver didn''t apologize.', 'Terrible experience overall. Would not recommend.', '["cold","late","dry","poor_communication"]', false, 'pending_review', NULL, NULL, false, NULL, '2024-01-15T13:45:00Z', '2024-01-15T13:45:00Z');
